// Get AI response and original data
const aiResponse = $input.first().json;
const originalData = $('Process Input').first().json;

// Extract the personalized opener
const opener = aiResponse.message?.content || aiResponse.text || "Something went wrong. Try again.";

// Clean up the opener
const cleanOpener = opener
  .replace(/^"|"$/g, '')
  .replace(/^'|'$/g, '')
  .trim();

// Create final response
const response = {
  success: true,
  opener: cleanOpener,
  prospect: originalData.first_name,
  company: originalData.company_name,
  next_steps: [
    "1. Copy the opener above",
    "2. Add your value prop (2-3 sentences max)",
    "3. Include clear CTA",
    "4. Send it"
  ],
  tips: [
    "Keep total email under 100 words",
    "Send Tuesday-Thursday, 8-10am",
    "Follow up in 3-5 days if no response",
    "Personalize the subject line too"
  ]
};

return { json: response };

// Get form data
const firstName = $json['Author\'s First Name']?.trim();
const companyName = $json['Company Name']?.trim();
const postContent = $json['LinkedIn Post Content']?.trim();

// Validate input
if (!firstName || !companyName || !postContent) {
  throw new Error('All fields are required');
}

if (postContent.length < 50) {
  throw new Error('Post too short. Need at least 50 characters for good personalization.');
}

// Clean the post content
const cleanedPost = postContent
  .replace(/\s+/g, ' ')
  .replace(/[\r\n]+/g, ' ')
  .trim();

return {
  json: {
    first_name: firstName,
    company_name: companyName,
    post_content: cleanedPost,
    timestamp: new Date().toISOString()
  }
};
